package com.discover.atlas.common.vo;

import java.util.HashSet;
import java.util.Set;

import javax.persistence.CascadeType;
import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.FetchType;
import javax.persistence.GeneratedValue;
import javax.persistence.GenerationType;
import javax.persistence.Id;
import javax.persistence.JoinColumn;
import javax.persistence.JoinTable;
import javax.persistence.ManyToMany;
import javax.persistence.Table;

@Entity
@Table(name = "buttonGroup")
public class ButtonGroup {

	@Id
	@GeneratedValue(strategy = GenerationType.IDENTITY)
	@Column(name = "buttongroup_id")
	private int buttondgroupid;
	private String buttongroupname;
	private boolean buttongroupwebmigrated;
	private String buttongroupclassname;
	private String buttongrouplob;
	@ManyToMany(fetch = FetchType.EAGER, cascade = CascadeType.ALL)
	@JoinTable(name = "links_Button_Group", joinColumns = {
			@JoinColumn(name = "buttongroup_id", nullable = false, updatable = false) }, inverseJoinColumns = {
			@JoinColumn(name = "links_id", nullable = false, updatable = false) })
	private Set<Links> links = new HashSet<Links>();

	public int getButtondgroupid() {
		return buttondgroupid;
	}

	public void setButtondgroupid(int buttondgroupid) {
		this.buttondgroupid = buttondgroupid;
	}

	public boolean isButtongroupwebmigrated() {
		return buttongroupwebmigrated;
	}

	public void setButtongroupwebmigrated(boolean buttongroupwebmigrated) {
		this.buttongroupwebmigrated = buttongroupwebmigrated;
	}

	public String getButtongroupname() {
		return buttongroupname;
	}

	public void setButtongroupname(String buttongroupname) {
		this.buttongroupname = buttongroupname;
	}

	public String getButtongroupclassname() {
		return buttongroupclassname;
	}

	public void setButtongroupclassname(String buttongroupclassname) {
		this.buttongroupclassname = buttongroupclassname;
	}

	public String getButtongrouplob() {
		return buttongrouplob;
	}

	public void setButtongrouplob(String buttongrouplob) {
		this.buttongrouplob = buttongrouplob;
	}

	public Set<Links> getLinks() {
		return links;
	}

	public void setLinks(Set<Links> links) {
		this.links = links;
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + buttondgroupid;
		result = prime * result + ((buttongroupclassname == null) ? 0 : buttongroupclassname.hashCode());
		result = prime * result + ((buttongrouplob == null) ? 0 : buttongrouplob.hashCode());
		result = prime * result + ((buttongroupname == null) ? 0 : buttongroupname.hashCode());
		result = prime * result + (buttongroupwebmigrated ? 1231 : 1237);
		result = prime * result + ((links == null) ? 0 : links.hashCode());
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		ButtonGroup other = (ButtonGroup) obj;
		if (buttondgroupid != other.buttondgroupid)
			return false;
		if (buttongroupclassname == null) {
			if (other.buttongroupclassname != null)
				return false;
		} else if (!buttongroupclassname.equals(other.buttongroupclassname))
			return false;
		if (buttongrouplob == null) {
			if (other.buttongrouplob != null)
				return false;
		} else if (!buttongrouplob.equals(other.buttongrouplob))
			return false;
		if (buttongroupname == null) {
			if (other.buttongroupname != null)
				return false;
		} else if (!buttongroupname.equals(other.buttongroupname))
			return false;
		if (buttongroupwebmigrated != other.buttongroupwebmigrated)
			return false;
		if (links == null) {
			if (other.links != null)
				return false;
		} else if (!links.equals(other.links))
			return false;
		return true;
	}
}
