package com.discover.atlas.common.vo;

import java.io.Serializable;

import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.GeneratedValue;
import javax.persistence.GenerationType;
import javax.persistence.Id;
import javax.persistence.Table;

@Entity
@Table(name = "links")
public class Links implements Serializable {

	@Id
	@GeneratedValue(strategy = GenerationType.IDENTITY)
	@Column(name = "links_id")
	private int linksid;
	private String linkname;
	private String linkpath;
	private String actiongroup;
	private boolean linkwebmigrated;
	private String linkclassname;

	public Links() {

	}

	public int getLinksid() {
		return linksid;
	}

	public void setLinksid(int linksid) {
		this.linksid = linksid;
	}

	public String getLinkname() {
		return linkname;
	}

	public void setLinkname(String linkname) {
		this.linkname = linkname;
	}

	public String getLinkpath() {
		return linkpath;
	}

	public void setLinkpath(String linkpath) {
		this.linkpath = linkpath;
	}

	public String getActiongroup() {
		return actiongroup;
	}

	public void setActiongroup(String actiongroup) {
		this.actiongroup = actiongroup;
	}

	public boolean isLinkwebmigrated() {
		return linkwebmigrated;
	}

	public void setLinkwebmigrated(boolean linkwebmigrated) {
		this.linkwebmigrated = linkwebmigrated;
	}

	public String getLinkclassname() {
		return linkclassname;
	}

	public void setLinkclassname(String linkclassname) {
		this.linkclassname = linkclassname;
	}

}
