package com.discover.atlas.util;

import java.util.HashMap;
import java.util.List;
import java.util.Map;

import com.discover.atlas.common.vo.ButtonGroup;
import com.discover.atlas.common.vo.Links;

public class AtlasWebSingleton {

	private static AtlasWebSingleton instance = null;
	private static Map<ButtonGroup, Map<String, List<Links>>> linksMap =null;
	
	private AtlasWebSingleton(){
		
		linksMap=new HashMap<>();

	}
	
	private static AtlasWebSingleton getInstance(){
		if(instance == null){
			instance = new AtlasWebSingleton();
		}
		return instance;
	}
	
	public static void setLinksMap(Map<ButtonGroup, Map<String, List<Links>>> buttonGroupMap ){
	
		getInstance().linksMap.putAll(buttonGroupMap);
	}
	
	public static  Map<ButtonGroup, Map<String, List<Links>>>  getLinksMap(){
		return linksMap;
		
	}
}
