package com.discover.atlas.util;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.context.event.ContextRefreshedEvent;
import org.springframework.context.event.EventListener;
import org.springframework.stereotype.Component;

import com.discover.atlas.common.vo.ButtonGroup;
import com.discover.atlas.common.vo.Links;
import com.discover.atlas.repository.LinksRepository;

@Component
public class LoadDataOnStartup {

	@Autowired
	private LinksRepository linksRepository;

	@EventListener
	public void onApplicationEvent(ContextRefreshedEvent event) {
		List<ButtonGroup> buttonGroups = linksRepository.findAll();
		Map<ButtonGroup, Map<String, List<Links>>> buttonGroupMap=new HashMap<>();
		if(!AtlasWebUtil.isEmpty(buttonGroups)){
			for(ButtonGroup buttonGroup:buttonGroups){
				if(!AtlasWebUtil.isEmpty(buttonGroup.getLinks())){
					for(Links link:buttonGroup.getLinks()){
						Map<String, List<Links>> linksMap=buttonGroupMap.get(buttonGroup);
						if(linksMap==null){
							linksMap=new HashMap<>();
						}
						List<Links> links=linksMap.get(link.getActiongroup());
						if(links==null){
							links=new ArrayList<>();	
						}
						links.add(link);
						linksMap.put(link.getActiongroup(),links);
						buttonGroupMap.put(buttonGroup, linksMap);
					}
				}
			}
		}
		AtlasWebSingleton.setLinksMap(buttonGroupMap);
	}
}
