
DROP TABLE IF EXISTS links;
DROP TABLE IF EXISTS button_Group;
DROP TABLE IF EXISTS links_Button_Group;

CREATE TABLE links
(
    links_id BIGINT AUTO_INCREMENT NOT NULL,
    linkname varchar(200) NOT NULL,
    linkpath varchar(200),
    actiongroup varchar(200) NOT NULL,
    linkclassname varchar(500),
    linkwebmigrated boolean NOT NULL,
   
    PRIMARY KEY (links_id)
    
   );
    
  CREATE TABLE button_Group
  (
  	 buttongroup_id BIGINT AUTO_INCREMENT NOT NULL,
  	 buttongroupname varchar(200) NOT NULL,
  	 buttongroupwebmigrated boolean NOT NULL,
  	 buttongrouplob varchar(200) NOT NULL,
  	 buttongroupclassname varchar(200) NOT NULL,
  	 
  	 PRIMARY KEY (buttongroup_id)
    );
    
  CREATE TABLE links_Button_Group
    (
 	 links_id int NOT NULL,
  	 buttongroup_id int NOT NULL,
  	
  	 PRIMARY KEY (buttongroup_id,links_id)
  
  );
    
    
    
    