import { NgModule } from '@angular/core';
import { Routes, RouterModule } from '@angular/router';

import { CustomerActionModule } from "./customeractionsmodule/customeractions.module";
import { LoanActionModule } from "./loanactionsmodule/loanactions.module";
import { AccountActionModule } from "./accountactionsmodule/accountactions.module";
import { PlanActionModule } from "./planactionsmodule/planactions.module";

import { CustomerActionsComponent } from "./customeractionsmodule/customeractions.component";
import { LoanActionsComponent } from "./loanactionsmodule/loanactions.component";
import { AccountActionsComponent } from "./accountactionsmodule/accountactions.component";
import { PlanActionsComponent } from "./planactionsmodule/planactions.component";

const routes: Routes = [
            { path: '', redirectTo: 'customeractions', pathMatch: 'full' },
     		{ path: 'customeractions',  component: CustomerActionsComponent },
     		{ path: 'loanactions',  component: LoanActionsComponent },
     		{ path: 'accountactions',  component: AccountActionsComponent },
     		{ path: 'planactions',  component: PlanActionsComponent }
];


@NgModule( {
    imports: [RouterModule.forRoot( routes ),CustomerActionModule,LoanActionModule,AccountActionModule,PlanActionModule],
    exports: [RouterModule]
} )


export class AppRoutingModule { }