import { Component, OnChanges, SimpleChanges, EventEmitter, Input, Output } from '@angular/core';

@Component( {
    selector: 'do-check',
    template: ``
} )

export class PhonemaintDocheckComponent implements OnChanges {
    @Input() cifobj: any;
    @Output() onUpdated = new EventEmitter<any>();

    changeDetected = false;
  
    ngOnChanges( changes: SimpleChanges ): void {

        for ( let propName in changes ) {
            let chng = changes[propName];
            this.onUpdated.emit(chng.currentValue);
        }
    }

}