import { Injectable } from '@angular/core';
import { Http, Response } from '@angular/http';
import 'rxjs/add/operator/map';





 
@Injectable()
export class PhonemaintService {

    constructor(
        private http: Http
    ) { }

    updateCustomerInfo( customer: any ) {

        return this.http.post( '/api/updateCustomerInfo', customer )
            .map(( response: Response ) => {
                return response.json() || {};
            } );
    }
    retrieveRevokedPhoneNumbers( customer: any ) {

        return this.http.post( '/api/retrieveRevokedPhoneNumbers', customer )
            .map(( response: Response ) => {
                return response.json() || {};
            } );
    }
    retrieveCountryCodeInfo  ( countryCode: any)
    {
        return this.http.post( '/api/retrieveCountryCodeInfo', countryCode )
        .map(( response: Response ) => {
            return response.json() || {};
        } );  
    }
 
  //  /revocationService/getRevokedPhoneList
}